f = ''
k = ''
L = 0
g = 0
for b in f:
    if b != '0':
        k = k + b
    elif b == '0':
        for t in k:
            if int(t)%2 == 0:
                g = g +1
        if g == len(k) or g == 0:
            if L<len(k):
                L = g
        k = ''
    g = 0

print(L)

